INSERT INTO `his_config_info` VALUES (0, 20, 'saas-gateway-prd.yaml', 'PRD_GROUP', '网关', 'server:\n  port: 4500\nspring:\n  application:\n    name: saas-gateway\n  jackson:\n    time-zone: GMT+8\n    date-format: yyyy-MM-dd HH:mm:ss\n  servlet:\n    multipart:\n      enabled: true #开启文件上传\n      max-file-size: 10MB #限制文件上传大小为10M\n      file-size-threshold: 10MB #单个文件大小限制\n      max-request-size: 200MB #总文件大小限制\n  cloud:\n    gateway:\n      default-filters:\n        - DedupeResponseHeader=Access-Control-Allow-Origin Access-Control-Allow-Credentials Access-Control-Max-Age Access-Control-Expose-Headers, RETAIN_UNIQUE\n      # 路由配置项，对应 RouteDefinition 数组\n      routes:\n        - id: saas-hair # 毛检服务\n          uri: lb://saas-hair\n          predicates: # 断言，作为路由的匹配条件，对应 RouteDefinition 数组\n            - Path=/hair/**\n          filters:\n            - StripPrefix=1\n            - name: CircuitBreaker\n              args:\n                name: circuitBreaker\n                fallbackUri: forward:/fallback\n        - id: saas-member # 会员\n          uri: lb://saas-member\n          predicates: # 断言，作为路由的匹配条件，对应 RouteDefinition 数组\n            - Path=/member/**\n          filters:\n            - StripPrefix=1\n            - name: CircuitBreaker\n              args:\n                name: circuitBreaker\n                fallbackUri: forward:/fallback\n        - id: saas-system\n          uri: lb://saas-system\n          predicates:\n            - Path=/system/**\n          filters:\n            - StripPrefix=1\n            - name: CircuitBreaker\n              args:\n                name: circuitBreaker\n                fallbackUri: forward:/fallback\n        - id: saas-applet\n          uri: lb://saas-applet\n          predicates:\n            - Path=/applet/**\n          filters:\n            - StripPrefix=1\n            - name: CircuitBreaker\n              args:\n                name: circuitBreaker\n                fallbackUri: forward:/fallback\n        - id: saas-a